document.addEventListener("DOMContentLoaded", renderQuestion);

questionsContainer.addEventListener("click", (e) => {
  //   Toggle option
  if (e.target.classList.contains("checkbox-card")) {
    const card = e.target.closest(".checkbox-card");
    const optionIndex = parseInt(card.dataset.id);
    const currentQuestion = questions[currentQuestionIndex];

    // Deselect all options in the current question
    currentQuestion.options.forEach((opt) => (opt.isSelected = false));

    // Select the clicked option
    currentQuestion.options[optionIndex].isSelected = true;

    renderQuestion();
  }

  if (e.target.classList.contains("submit-button")) {
    e.preventDefault();
  }

  if (
    e.target.classList.contains("submit-button") &&
    questions[currentQuestionIndex].options
  ) {
    submitCheckboxQuestion();
  }

  if (
    e.target.classList.contains("submit-button") &&
    !questions[currentQuestionIndex].options
  ) {
    submitFormQuestion();
  }

  if (e.target.classList.contains("previous-button")) {
    if (currentQuestionIndex > 0) {
      currentQuestionIndex--;
      renderQuestion();
    }
  }
});

function getCheckboxQuestionTemplate(option, index) {
  const html = `<div class="checkbox-card ${option.isSelected ? "checkbox-card--active" : ""}" data-id="${index}">
              <div class="checkbox-card__content" data-id="${index}">
                <div class="checkbox-card__check">
                  <i class="ri-check-line"></i>
                </div>
                ${option.icon}
                <div class="checkbox-card__text">
                  <h3 class="checkbox-card__title">${option.title}</h3>
                  ${
                    option.subtitle
                      ? `<p class="checkbox-card__description text-muted">
                    <small
                      >${option.subtitle}</small
                    >
                  </p>`
                      : ""
                  }
                </div>
              </div>
            </div>`;

  return html;
}

